import React, { useState } from 'react';

const Carousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const images = [
    'http://fakeimg.pl/2000x800/0079D8/fff/?text=Without',
    'http://fakeimg.pl/2000x800/DA5930/fff/?text=JavaScript',
    'http://fakeimg.pl/2000x800/F90/fff/?text=Carousel',
  ];

  const nextSlide = () => {
    setActiveIndex((activeIndex + 1) % images.length);
  };

  const prevSlide = () => {
    setActiveIndex((activeIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="relative mt-10">
      <div className="carousel">
        <div className="carousel-inner">
          {images.map((image, index) => (
            <div
              key={index}
              className={`carousel-item absolute transition-opacity duration-600 ${
                index === activeIndex ? 'opacity-100' : 'opacity-0'
              }`}
            >
              <img src={image} className="block max-w-full" alt={`Slide ${index + 1}`} />
            </div>
          ))}
        </div>

        <div className="carousel-controls">
          <label
            className="carousel-control prev left-2% cursor-pointer"
            onClick={prevSlide}
          >
            &#8249
          </label>
          <label
            className="carousel-control next right-2% cursor-pointer"
            onClick={nextSlide}
          >
            &#8250
          </label>
        </div>
      </div>

      <ol className="carousel-indicators list-none m-0 p-0 absolute bottom-2 left-0 right-0 text-center z-10">
        {images.map((_, index) => (
          <li key={index}>
            <label
              className={`carousel-bullet text-white cursor-pointer text-3xl ${
                index === activeIndex ? 'text-blue-500' : ''
              }`}
            >
              &#8226
            </label>
          </li>
        ))}
      </ol>
    </div>
  );
};

export default Carousel;
