import HeroImage from './BGmain1.jpg';
export const Hero = () => {
    return (
  
<div className="relative">
      <div className=" whitespace-nowrap  bg-gray-200 m-24 text-left ">
      <h1 className="drop-shadow-lg  p-4 lg:text-7xl sm:text-lg sm:w-auto font-sans">Step to Wellness,</h1>
      <h1 className="drop-shadow-lg lg:text-9xl sm:w-auto sm:text-lg font-semibold font-sans ">One <span className=' drop-shadow-xl bg-gradient-to-r  from-secondary to-primary bg-clip-text text-transparent'>Foot</span> at a Time.</h1>
      <div class=" mt-40 w-full">
      <button
            type="button"
            class="rounded bg-gradient-to-bl sm:self-center from-primary to-secondary p-4 text-lg font-medium uppercase leading-normal text-white shadow-[0_4px_9px_-4px_#3b71ca] transition duration-150 ease-in-out hover:bg-primary-600 hover:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.3),0_4px_18px_0_rgba(59,113,202,0.2)] focus:bg-primary-600 focus:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.3),0_4px_18px_0_rgba(59,113,202,0.2)] focus:outline-none focus:ring-0 active:bg-primary-700 active:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.3),0_4px_18px_0_rgba(59,113,202,0.2)] dark:shadow-[0_4px_9px_-4px_rgba(59,113,202,0.5)] dark:hover:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.2),0_4px_18px_0_rgba(59,113,202,0.1)] dark:focus:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.2),0_4px_18px_0_rgba(59,113,202,0.1)] dark:active:shadow-[0_8px_9px_-4px_rgba(59,113,202,0.2),0_4px_18px_0_rgba(59,113,202,0.1)]">
            Book a Consultation
          </button>
      </div>
      </div>
      
    </div>      

);
};
export default Hero;
